<script>
const popURL =
  "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=04c35731a5ee918f014970082a0088b1&page=1";
const bg = "https://image.tmdb.org/t/p/w1280";
const searchURL =
  "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query=";
const imgTest =
  "https://image.tmdb.org/t/p/w1280/ugZW8ocsrfgI95pnQ7wrmKDxIe.jpg";
export default {
  data: () => ({
    imgTest: imgTest,
    imgs: [],
    searchTerm: "",
    sideitems: [
      {
        label: "Popular",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Top Rated",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Upcoming",
        src:
          "data:image/png;base64,",
      },
    ],
    sidegenres: [
      {
        label: "Action",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Adventure",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Animation",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Comedy",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Crime",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Documentary",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Drama",
        src:
          "data:image/png;base64,",
      },
      {
        label: "Family",
        src:
          "data:image/png;base64,",
      },
    ],
  }),
  methods: {
    search() {
      if (this.searchTerm === "") {
        return;
      }
      fetch(`${searchURL}${this.searchTerm}`)
        .then((val) => val.json())
        .then((movies) => {
          console.log("movies", movies);
          this.imgs = this.parseImgResponse(movies);
        });
    },
    parseImgResponse(movies) {
      return movies.results.reduce((acc, movie) => {
        if (!movie.poster_path) {
          return acc;
        }
        acc.push({
          poster: `${bg}${movie.poster_path}`,
          date: movie.release_date,
          title: movie.title,
        });
        return acc;
      }, []);
    },
  },
  mounted() {
    fetch(popURL)
      .then((info) => info.json())
      .then((movies) => {
        console.log(movies);
        this.imgs = this.parseImgResponse(movies);
      });
  },
};
</script>

